import axios from "axios";
import About from "./About";
import Home from "./Launcher";
import Notfound from "./Notfound";
import { useState,useEffect } from "react";
import './Button.css'

function Dashboard() {
    const url="http://127.0.0.1/emps"
    var [emps,setEmps]=useState([]);
    const FetchRecords=()=>{
        axios.get("http://127.0.0.1:9999/emps").then((result)=>{
            setEmps(result.data);
        })
    }
    FetchRecords();

    const DeleteRecord=(No)=>{
        var deleteurl=url+No
        axios.delete(deleteurl).then((result)=>{
            console.log(result)
        })
    }
   return(<>
   <div className="table table-responsive">
    <table className="table table-bordered">
        <thead>
            <tr>
            <th>No</th>
            <th>Name</th>
            <th>Address</th>
            <th colSpan={2}></th>

            </tr>
        </thead>
        <tbody>
           {
            emps.map((emp)=>{
                return(<tr key={emp.No}>
                    <td>
                        {emp.No}
                    </td>
                    <td>
                        {emp.Name}
                    </td>
                    <td>
                        {emp.Address}
                    </td>
                    <td>
                        <button className="btn btn-primary" onClick={()=>{DeleteRecord(emp.No)}} >Edit</button>
                    </td>
                    <td>
                        <button className="btn btn-danger">Delete</button>
                    </td>
                </tr>)
            })
           }
        </tbody>
    </table>




   </div>



   </>)

}

export default Dashboard;
