import '../node_modules/bootstrap/dist/css/bootstrap.min.css';
import axios from 'axios';
import { useState,useEffect } from 'react';


function Dashboard() {

    const [emps,setEmps]=useState([]);
    const [emp,setEmp]=useState({});
    const [message,setMessage]=useState("");

    const url='http://127.0.0.1:9999/emps';
    const FetchRecord=()=>{
        axios.get(url).then((result)=>{setEmps(result.data)})

    }

    useEffect(()=>{FetchRecord()},[]);


    const RemoveRecord=(No)=>
    {
        console.log(No);
        const deleteUrl=url+"/"+No
        axios.delete(deleteUrl).then((result)=>{
            if(result.data.affectedRows!==undefined&&result.data.affectedRows>0)
            {
                setMessage("Data Delete Successfully");
                FetchRecord();
            }
        })

    }










    return ( <div className='container'>
        <div className='table-responsive'>
        <table className='table table-bordered'>
            <thead>
                <tr>
                    <th>
                        No
                    </th>
                    <th>
                        Name
                    </th>
                    <th>
                        Address
                    </th>
                    <th></th>
                    <th></th>
                </tr>
            </thead>
            <tbody>
                {
                    emps.map((emp)=>{
                        return (<tr key={emp.No}>
                            <td>{emp.No}</td>
                            <td>{emp.Name}</td>
                            <td>{emp.Address}</td>
                            <td><button className='btn btn-warning'>Edit</button></td>
                            <td><button className='btn btn-danger' onClick={()=>{RemoveRecord(emp.No)}}>Delete</button></td>
                        </tr>)
                    })

                }
            </tbody>
            <tbody></tbody>
        </table>
        </div>

    </div>);
}

export default Dashboard;
