import axios from "axios";
import About from "./About";
import Home from "./Launcher";
import Notfound from "./Notfound";
import { useState,useEffect } from "react";
import './Button.css'

function Dashboard() {
    const url="http://127.0.0.1:9999/emps"
    var [emps,setEmps]=useState([]);
    var [emp,setEmp]=useState({No:0,Name:"",Address:""});
    const FetchRecords=()=>{
        axios.get(url).then((result)=>{
            setEmps(result.data);
        })
    }
    useEffect(()=>{FetchRecords()},[emps])
    const DeleteRecord=(No)=>{
        var deleteurl=url+"/"+No;
        console.log(deleteurl)
        axios.delete(deleteurl).then((result)=>{
            console.log(result);
        })
    }
    const onTextChange=(args)=>{
        emp[args.target]


    }
   return(<>
   <div >
    <hr />

    <center>
    <table>
        <tbody>
            <tr>
                <td>No</td>
                <td><input type="number"  value={emp.No}onChange={onTextChange}></input></td>
            </tr>
            <tr>
                <td>Name</td>
                <td><input type="text"/></td>
            </tr>
            <tr>
                <td>Address</td>
                <td><input type="text"/></td>
            </tr>
        </tbody>
    </table>
    </center>
    <hr />
    <table className="table table-bordered">
        <thead>
            <tr>
            <th>No</th>
            <th>Name</th>
            <th>Address</th>
            <th colSpan={2}></th>

            </tr>
        </thead>
        <tbody>
           {
            emps.map((emp)=>{
                return(<tr key={emp.No}>
                    <td>
                        {emp.No}
                    </td>
                    <td>
                        {emp.Name}
                    </td>
                    <td>
                        {emp.Address}
                    </td>
                    <td>
                        <button className="btn btn-primary">Edit</button>
                    </td>
                    <td>
                        <button className="btn btn-danger" onClick={()=>{DeleteRecord(emp.No)}}>Delete</button>
                    </td>
                </tr>)
            })
           }
        </tbody>
    </table>




   </div>



   </>)

}

export default Dashboard;
